import { useState , useCallback } from 'react';
import { useParams } from 'react-router';
import { useEffect } from 'react/cjs/react.development';
import useHttp from '../../hooks/use-http';
import { getAllComments } from '../../lib/api';
import LoadingSpinner from '../UI/LoadingSpinner';

import classes from './Comments.module.css';
import NewCommentForm from './NewCommentForm';

const Comments = () => {
  const [isAddingComment, setIsAddingComment] = useState(false);
  const params=useParams();
  const{quoteId}=params.quoteid;
  const {sendRequest , status , data:loadedComments} = useHttp(getAllComments);

  useEffect(()=>{
    sendRequest(quoteId);
  },[sendRequest , quoteId]);

  let Comments;
  if(status==='pending')
  {
    Comments=<div><LoadingSpinner /></div>
  }
  else if(status==='completed' && loadedComments &&loadedComments.length>0 )
  {
    Comments=loadedComments;
  }
  else if (status==='completed' && !loadedComments || loadedComments.length===0 )
  {
    Comments=<div className='centered'>No Comments found.</div>
  }

  const startAddCommentHandler = () => {
    setIsAddingComment(true);
  };

  const addCommentHandler = useCallback({

  },[])

  return (
    <section className={classes.comments}>
      <h2>User Comments</h2>
      {!isAddingComment && (
        <button className='btn' onClick={startAddCommentHandler}>
          Add a Comment
        </button>
      )}
      {isAddingComment && <NewCommentForm  quoteId={params.quoteId} onAddComment={addCommentHandler}/>}
      {Comments}
    </section>
  );
};

export default Comments;
